import { Component } from 'react';
// import ReactAudioPlayer from 'react-audio-player';

class Index extends Component {
    constructor(props) {
        super(props);

        this.state = {
            audio: null
        }
    }

    componentDidMount() {
        let streamURL = "https://player.audiovox.pw/proxy/6768/;";
        let audio = document.getElementById("main-stream");

        this.setState({
            audio: document.getElementById("main-stream")
        })

        audio.innerHTML = '<source id="main-stream-source" src="' + streamURL + '">';

        
    }

    render() {
        // let toggle = () => {
        //     let el = document.querySelector('.react-audio-player');

        //     if(el.volume == 0) {
        //         el.volume = 1.0;
        //     } else {
        //         el.volume = 0;
        //     }
        // }
        return (
            <>
                <div className="background"></div>
                <div className="radial"></div>

                <div className="container">
                    <div className="player">
                        <div className="radio-status">
                            <div className="radio-status-data">AutoDJ</div>
                            <div className="radio-status-data">Zona Livre</div>
                        </div>

                        <div className="radio-slider">
                            <div className="radio-slider-control"></div>
                        </div>

                        <div className="radio-status-data-listeners">
                            7
                            <div className="radio-status-data-listeners-text">ouvintes</div>
                        </div>
                    </div>

                    <div className="center">
                        <div className="logo">
                            <div className="vacancies">Vagas</div>
                            <div className="control play" onClick={() => toggle() }></div>
                            <div className="team">Equipe</div>
                        </div>
                    </div>
                </div>

                {/* <ReactAudioPlayer
                    src="https://player.audiovox.pw/proxy/6768/;type=mp3"
                    autoPlay
                    controls
                    ref={this.state.audio}
                /> */}

                <audio id="main-stream" preload="none" type="audio/mp3" volume="0" muted controls />
            </>
        );
    }
}

export default Index;
