import { Component, useState, useEffect  } from 'react';
import ReactAudioPlayer from 'react-audio-player';
import ReactSlider from 'react-slider';

let Index = () => {
    const [volume, setVolume] = useState(0);
    const [valueNow, setvalueNow] = useState(0);

    useEffect(() => {
        let audio = document.querySelector('.react-audio-player');
        console.log(`Volume ${volume}`);
    });

    let toggle = () => {
        let audio = document.querySelector('.react-audio-player');
        
        if(audio.volume == 0) {
            setVolume(1);
            audio.volume = 1;
        } else {
            setVolume(0);
            audio.volume = 0;
        }
    }

    return (
        <>
            <div className="background"></div>
            <div className="radial"></div>

            <div className="container">
                <div className="player">
                    <div className="radio-status">
                        <div className="radio-status-avatar"></div>
                        <div className="radio-status-data" onClick={() => setVolume(0)}>AutoDJ</div>
                        <div className="radio-status-data" onClick={() => setVolume(100)}>Zona Livre</div>
                    </div>

                    {/* <RadioSlider /> */}
                    <ReactSlider
                        className="radio-slider"
                        trackClassName="radio-slider-control"
                        defaultValue={100}
                        renderThumb={(props, state) => <div {...props}></div>}
                    />

                    {/* <div className="radio-slider">
                        <div className="radio-slider-control"></div>
                    </div> */}

                    <div className="radio-status-data-listeners">
                        7
                        <div className="radio-status-data-listeners-text">ouvintes</div>
                    </div>
                </div>

                <div className="center">
                    <div className="logo">
                        <div className="vacancies">Vagas</div>
                        <div className="control play" onClick={() => toggle() }></div>
                        <div className="team">Equipe</div>
                    </div>
                </div>
            </div>

            <ReactAudioPlayer
                src="https://player.audiovox.pw/proxy/6768/;type=mp3"
                autoPlay
                controls
                listenInterval={"1000"}
            />
        </>
    );
}

export default Index;
