import { Component } from 'react';
import ReactAudioPlayer from 'react-audio-player';
import ReactSlider from 'react-slider';
import fetch from 'isomorphic-unfetch';

import xmlToJson from '../helpers/xmlToJson';

let toggle = () => {
    let audio = document.querySelector('.react-audio-player');
    
    if(audio.volume == 0) {
        audio.volume = 1;
    } else {
        audio.volume = 0;
    }
}

const Index = (props) => {
    return (
        <>
            <div className="background"></div>
            <div className="radial"></div>

            <div className="container">
                <div className="player">
                    <div className="radio-status">
                        <div className="radio-status-avatar"></div>
                        <div className="radio-status-data">AutoDJ</div>
                        <div className="radio-status-data">Zona Livre</div>
                    </div>

                    {/* <RadioSlider /> */}
                    <ReactSlider
                        className="radio-slider"
                        trackClassName="radio-slider-control"
                        onChange={(value) => document.querySelector('.react-audio-player').volume = (value / 100)}
                        defaultValue={100}
                    />

                    {/* <div className="radio-slider">
                        <div className="radio-slider-control"></div>
                    </div> */}

                    <div className="radio-status-data-listeners">
                        7
                        <div className="radio-status-data-listeners-text">ouvintes</div>
                    </div>
                </div>

                <div className="center">
                    <div className="logo">
                        <div className="vacancies">Vagas</div>
                        <div className="control play" onClick={() => toggle() }></div>
                        <div className="team">Equipe</div>
                    </div>
                </div>
            </div>

            <ReactAudioPlayer
                src="https://player.audiovox.pw/proxy/6768/;type=mp3"
                autoPlay
                controls
                listenInterval={"1000"}
            />
        </>
    );
}

Index.getInitialProps = async function() {
    fetch('https://painel.audiovox.pw/api/Njc2OCsw')
        .then(response => response.text())
        .then(str => (new window.DOMParser()).parseFromString(str, "text/xml"))
        .then(data => {
            let status = xmlToJson(data).info;

            document.querySelector('.avatar').setAttribute('src', `https://www.habbo.com.br/habbo-imaging/avatarimage?hb=image&user=${status.titulo['#text']}&headonly=0&direction=2&head_direction=2&action=&gesture=&size=s&headonly=1`);
            document.querySelector('.locutor').textContent = status.titulo['#text'];
            document.querySelector('.programa').textContent = status.genero['#text'];
        })
    const res = await fetch('https://api.tvmaze.com/search/shows?q=batman');
    const data = await res.json();
  
    console.log(`Show data fetched. Count: ${data.length}`);
  
    return {
      shows: data.map(entry => entry.show)
    };
};

export default Index;
