import { Component } from 'react';
import ReactHowler from 'react-howler'
// import ReactAudioPlayer from 'react-audio-player';
import ReactSlider from 'react-slider';
import { radioUpdate } from '../helpers/radio';



class Index extends Component {
    constructor(props) {
        super(props);

        this.state = {
            stream: "https://player.audiovox.pw/proxy/6768/;type=mp3",
            status: 'stop',
            format: ['mp3', 'acc'],
            volume: 100
        }
    }

    componentDidMount() {
        radioUpdate('all', document);
    }

    setSlider(value) {
        if(value > 0) {
            this.sound.volume(parseFloat(value));
        }
    }

    toggle() {
        if(window.Howler._muted == true) {
            window.Howler.mute(false);
            this.setState({ status: 'stop' });
        } else {
            window.Howler.mute(true);
            this.setState({ status: 'play' });
        }
    }

    render() {
        let { stream, format, status, volume } = this.state;
        let { props }= this;

        return (
            <>
                <ReactHowler
                    src={stream}
                    autoplay={true}
                    volume={1.0}
                    html5={true}
                    format={format}
                    ref={(ref) => (this.sound = ref)}
                />

                <div className="background"></div>
                <div className="radial"></div>

                <div className="player">
                    <div className="radio-status">
                        <div className="radio-status-avatar"></div>
                        <div className="radio-status-data">...</div>
                        <div className="radio-status-data">...</div>
                    </div>

                    {/* <RadioSlider /> */}
                    <ReactSlider
                        className="radio-slider"
                        trackClassName="radio-slider-control"
                        onChange={(value) => this.setSlider(value / 100)}
                        min={0}
                        defaultValue={volume}
                    />

                    <div className="radio-status-data-listeners">
                        ?
                        <div className="radio-status-data-listeners-text">ouvintes</div>
                    </div>
                </div>

                <div className="center">
                    <div className="logo">
                        <div className="vacancies" onClick={() => alert('Not now!')}>Vagas</div>
                        <div className={`control ${status}`} onClick={() => this.toggle() }></div>
                        <div className="team" onClick={() => alert('Not now!')}>Equipe</div>
                    </div>
                </div>
            </>
        );
    }
}

// Index.getInitialProps = async function() {
//     const res  = await fetch('http://serv3.audiovox.pw:6768/stats?sid=1&json=1');
//     const data = await res.json();

//     return {
//         radio: data
//     };
// };

export default Index;
