import { Component } from 'react';

class Index extends Component {
    constructor(props) {
        super(props);

        this.state = {
            audio: new Audio('https://player.audiovox.pw/proxy/6768/;type=mp3'),
            isPlaying: false
        }
    }

    componentDidMount() {
        let colors = ['#34BAE7', '#7ACF25', '#FF2F34', '#F935EA', '#CEAE31', '#999999'];
        let last = 0;

        let changeColor = (color) => document.querySelector('body').style.backgroundColor = color;

        changeColor(colors[colors.length - 1]);

        setInterval(function() {
            if(last == colors.length) {
                last = 0;
            }
            
            changeColor(colors[last]);

            last++;
        }, 500);

        this.state.audio.addEventListener('timeupdate', this.ontimeupdate);
    }

    ontimeupdate() {
        var el = this.state.audio;
        var number = el.currentTime / el.duration * 100;

        this.update(number);

        if (number === 100) {
            return this.setState({
                isPlaying: false
            });
        }
    }

    toggle(e) {
        e.preventDefault();
        var state = this.state.audio;
        var isPlaying = this.state.isPlaying;

        this.setState({
            isPlaying: !isPlaying
        });

        if (isPlaying) {
            return state.pause();
        }
        return state.play();
    }

    update(progress) {
        var context = this.refs.manager.getDOMNode().getContext('2d');

        var percent = Math.min(progress, 100);
        var ratio = window.devicePixelRatio || 1;

        var size = this.refs.manager.getDOMNode().width / ratio;
        var half = size / 2;
        var x = half;
        var y = half;
        var rad = half - 1;

        var angle = Math.PI * 2 * (percent / 100);
        context.clearRect(0, 0, size, size);

        context.strokeStyle = '#00bbff';
        context.beginPath();
        context.arc(x, y, rad, 0, angle, false);
        context.stroke();
    }

    render() {
        var className = this.state.isPlaying ? 'audio playing' : 'audio paused';

        return (
            <>
                <div className="background"></div>
                <div className="radial"></div>

                <div className="container">
                    <div className="player">
                        <div className="radio-status">
                            <div className="radio-status-data">AutoDJ</div>
                            <div className="radio-status-data">Zona Livre</div>
                        </div>

                        <div className="radio-slider">
                            <div className="radio-slider-control"></div>
                        </div>

                        <div className="radio-status-data-listeners">
                            7
                            <div className="radio-status-data-listeners-text">ouvintes</div>
                        </div>
                    </div>

                    <div className="center">
                        <div className="logo">
                            <div className="vacancies">Vagas</div>
                            <div className="control play"></div>
                            <div className="team">Equipe</div>
                        </div>
                    </div>
                </div>

                <canvas width="57" height="57" ref="manager" size="53" />
            </>
        );
    }
}

export default Index;
