import { Component } from 'react';
import ReactHowler from 'react-howler'
// import ReactAudioPlayer from 'react-audio-player';
import ReactSlider from 'react-slider';
import xmlToJson from '../helpers/xmlToJson';

class Index extends Component {
    constructor(props) {
        super(props);

        this.state = {
            stream: "https://player.audiovox.pw/proxy/6768/;type=mp3",
            status: 'stop',
            format: ['mp3', 'acc'],
            volume: 100,
            locutor: '...',
            programa: '...',
            ouvintes: '?' 
        }
    }

    radioNewStatus(type, newText){
        switch (type) {
            case 'locutor':
                this.setState('locutor', newText);
                break;
            case 'programa':
                this.radioNewStatus('programa', status.genero['#text']);
                break;
            case 'ouvintes':
                this.radioNewStatus('ouvintes', status.ouvintes_conectados['#text']);
                break;
        }
        this.setState(type, newText);
    }

    radioUpdate(action) {
        if (action == 'avatar' || action == 'all') {
            this.radioNewStatus('avatar', 'AutoDJ');
        } else {
            this.radioNewStatus(action, '...');
        }

        fetch('https://painel.audiovox.pw/api/Njc2OCsw').then(response => response.text()).then(str => (new window.DOMParser()).parseFromString(str, "text/xml")).then(data => {
            let status = xmlToJson(data).info;
            switch (action) {
                case 'all':
                    this.radioNewStatus('avatar', status.titulo['#text']);
                    this.radioNewStatus('locutor', status.titulo['#text']);
                    this.radioNewStatus('programa', status.genero['#text']);
                    this.radioNewStatus('ouvintes', status.ouvintes_conectados['#text']);
                    break;
                case 'locutor':
                    this.radioNewStatus('avatar', status.titulo['#text']);
                    this.radioNewStatus('locutor', status.titulo['#text']);
                    break;
                case 'programa':
                    this.radioNewStatus('programa', status.genero['#text']);
                    break;
                case 'ouvintes':
                    this.radioNewStatus('ouvintes', status.ouvintes_conectados['#text']);
                    break;
            }
        });
    }

    componentDidMount() {
        this.radioUpdate('all');
    }

    setSlider(value) {
        if(value > 0) {
            this.sound.volume(parseFloat(value));
        }
    }

    toggle() {
        if(window.Howler._muted == true) {
            window.Howler.mute(false);
            this.setState({ status: 'stop' });
        } else {
            window.Howler.mute(true);
            this.setState({ status: 'play' });
        }
    }

    render() {
        let { stream, format, status, volume } = this.state;
        let { props }= this;

        return (
            <>
                <ReactHowler
                    src={stream}
                    autoplay={true}
                    volume={1.0}
                    html5={true}
                    format={format}
                    ref={(ref) => (this.sound = ref)}
                />

                <div className="background"></div>
                <div className="radial"></div>

                <div className="player">
                    <div className="radio-status">
                        <div className="radio-status-avatar"></div>
                        <div className="radio-status-data">{this.state.locutor}</div>
                        <div className="radio-status-data">{this.state.programa}</div>
                    </div>

                    {/* <RadioSlider /> */}
                    <ReactSlider
                        className="radio-slider"
                        trackClassName="radio-slider-control"
                        onChange={(value) => this.setSlider(value / 100)}
                        min={0}
                        defaultValue={volume}
                    />

                    <div className="radio-status-data-listeners" onClick={() => this.radioUpdate('ouvintes')}>
                        {this.state.ouvintes}
                        <div className="radio-status-data-listeners-text">ouvintes</div>
                    </div>
                </div>

                <div className="center">
                    <div className="logo">
                        <div className="vacancies" onClick={() => alert('Not now!')}>Vagas</div>
                        <div className={`control ${status}`} onClick={() => this.toggle() }></div>
                        <div className="team" onClick={() => alert('Not now!')}>Equipe</div>
                    </div>
                </div>
            </>
        );
    }
}

