import { Component } from 'react';
// import ReactAudioPlayer from 'react-audio-player';

let play = () => {
    let audio = document.getElementById("main-stream");
    var promise = audio.play();
    audio.volume = 1;
    audio.muted = false;

    if (promise !== undefined) {
        promise.then(_ => {
            console.log("[PlayerFrame] Play successful");

            localStorage.setItem('radio_play', 'allowed');
        }).catch(error => {
            console.log("[PlayerFrame] Play failed", error);

            localStorage.setItem('radio_play', 'not_allowed');
        });
    }
}

class Index extends Component {
    componentDidMount() {
        let streamURL = "https://player.audiovox.pw/proxy/6768/;";
        let audio = document.getElementById("main-stream");
        audio.innerHTML = '<source id="main-stream-source" src="' + streamURL + '">';

        play();
    }

    play() {
        let audio = document.getElementById("main-stream");
        var promise = audio.play();
        audio.volume = 1;
        audio.muted = false;

        if (promise !== undefined) {
            promise.then(_ => {
                console.log("[PlayerFrame] Play successful");

                localStorage.setItem('radio_play', 'allowed');
            }).catch(error => {
                console.log("[PlayerFrame] Play failed", error);

                localStorage.setItem('radio_play', 'not_allowed');
            });
        }
    }

    pause() {
        let audio = document.getElementById("main-stream");
        audio.volume = 0;
        audio.muted = true;

        localStorage.setItem('radio_play', 'paused');
    }

    toggle() {
        let audio = document.getElementById("main-stream");
        if(audio.volume == 0 || audio.muted == true) {
            this.play();
        } else {
            this.pause();
        }
    }

    render() {
        return (
            <>
                <div className="background"></div>
                <div className="radial"></div>

                <div className="container">
                    <div className="player">
                        <div className="radio-status">
                            <div className="radio-status-data">AutoDJ</div>
                            <div className="radio-status-data">Zona Livre</div>
                        </div>

                        <div className="radio-slider">
                            <div className="radio-slider-control"></div>
                        </div>

                        <div className="radio-status-data-listeners">
                            7
                            <div className="radio-status-data-listeners-text">ouvintes</div>
                        </div>
                    </div>

                    <div className="center">
                        <div className="logo">
                            <div className="vacancies">Vagas</div>
                            <div className="control play" onClick={() => this.toggle() }></div>
                            <div className="team">Equipe</div>
                        </div>
                    </div>
                </div>

                {/* <ReactAudioPlayer
                    src="https://player.audiovox.pw/proxy/6768/;type=mp3"
                    autoPlay
                    controls
                    ref={this.state.audio}
                /> */}

                <audio id="main-stream" preload="none" type="audio/mp3" autoPlay volume="0" muted controls />
            </>
        );
    }
}

export default Index;
